package com.koso.triplogger.converterimport androidx.room.TypeConverterimport com.koso.triplogger.model.TripFileReferenceimport com.koso.triplogger.model.TripFileReferenceJsonAdapterimport com.squareup.moshi.Moshiclass TripReferenceConverter {    /**     * The moshi json adapter for TripFileReference     */    val adapter = TripFileReferenceJsonAdapter(Moshi.Builder().build())    @TypeConverter    fun fromJson(value: String?): TripFileReference? {        return if(value == null) null else adapter.fromJson(value)    }    @TypeConverter    fun toJson(ref: TripFileReference?): String? {        return if(ref == null) null else adapter.toJson(ref)    }}