package com.koso.triplogger.dbimport androidx.lifecycle.LiveDataimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.withContextclass DataSetRepository(private val dao: DataSetRawDao) {    val all: LiveData<List<DataSetRaw>> = dao.getAll()    suspend fun getAllSuspend() = withContext(Dispatchers.IO) { dao.getAllSuspend() }    suspend fun insert(dataset: DataSetRaw) = withContext(Dispatchers.IO) {        dao.insert(dataset)    }}