package com.koso.triplogger.dbimport androidx.lifecycle.LiveDataimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.withContextclass DataSetRepository(private val dao: DataSetRawDao) {    val all: LiveData<List<DataSetRaw>> = dao.getAll()    suspend fun getAllSuspend() = withContext(Dispatchers.IO) { dao.getAllSuspend() }    suspend fun getPeriodSuspend(from: Long, to: Long) =        withContext(Dispatchers.IO) { dao.getPeriodSuspend(from, to) }    suspend fun insert(dataset: DataSetRaw) = withContext(Dispatchers.IO) {        dao.insert(dataset)    }    suspend fun clear() {        dao.deleteAll()    }}