package com.koso.triplogger.dbimport android.content.Contextimport androidx.room.Databaseimport androidx.room.Roomimport androidx.room.RoomDatabaseimport androidx.room.TypeConverters@Database(entities = arrayOf(TripRaw::class, DataSetRaw::class), version = 1, exportSchema = false)@TypeConverters(TripTypeConverter::class)abstract class TripLoggerRoomDatabase : RoomDatabase() {    abstract fun tripDao(): TripRawDao    abstract fun datasetDao(): DataSetRawDao    companion object {        @Volatile        private var INSTANCE: TripLoggerRoomDatabase? = null        fun getDatabase(context: Context): TripLoggerRoomDatabase {            val tempInstance = INSTANCE            if (tempInstance != null){                return tempInstance            }            synchronized(this){                val instance = Room.databaseBuilder(                    context.applicationContext,                    TripLoggerRoomDatabase::class.java,                    "triplogger_database"                ).build()                INSTANCE = instance                return instance            }        }    }}