package com.koso.triplogger.dbimport android.content.Contextimport androidx.room.*import androidx.sqlite.db.SupportSQLiteOpenHelper@Database(entities = arrayOf(TripRaw::class, DataSetRaw::class), version = 1, exportSchema = false)abstract class TripLoggerRoomDatabase : RoomDatabase() {    abstract fun tripDao(): TripRawDao    abstract fun datasetDao(): DataSetRawDao    companion object {        @Volatile        private var INSTANCE: TripLoggerRoomDatabase? = null        fun getDatabase(context: Context): TripLoggerRoomDatabase {            val tempInstance = INSTANCE            if (tempInstance != null){                return tempInstance            }            synchronized(this){                val instance = Room.databaseBuilder(                    context.applicationContext,                    TripLoggerRoomDatabase::class.java,                    "triplogger_database"                ).build()                INSTANCE = instance                return instance            }        }    }    override fun createOpenHelper(config: DatabaseConfiguration?): SupportSQLiteOpenHelper {        TODO("Not yet implemented")    }    override fun createInvalidationTracker(): InvalidationTracker {        TODO("Not yet implemented")    }    override fun clearAllTables() {        TODO("Not yet implemented")    }}