package com.koso.triplogger.dbimport android.content.Contextimport androidx.room.Entityimport androidx.room.PrimaryKeyimport com.koso.triplogger.io.TripFileHelperimport com.koso.triplogger.model.Trip@Entity(tableName = "triplogger_trip_table")class TripRaw(    @PrimaryKey(autoGenerate = true)    val id: Long,    val starttime: Long,    val endtime: Long,    val filename: String,    val distance: Int) {    suspend fun toTrip(context: Context): Trip {        val trip = TripFileHelper.readFromLocal(context, filename)        return Trip(id, starttime, endtime, trip?.nodes ?: listOf(), distance)    }    companion object {        fun fromTrip(filename: String, trip: Trip): TripRaw {            return TripRaw(                starttime = trip.starttime,                endtime = trip.endtime,                filename = filename,                id = trip.id,                distance = trip.distance            )        }    }}