package com.koso.triplogger.dbimport androidx.lifecycle.LiveDataimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Query@Daointerface TripRawDao {    @Query("SELECT * from triplogger_trip_table ORDER BY id ASC")    fun getAll(): LiveData<List<TripRaw>>    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insert(trip: TripRaw)    @Query("DELETE FROM triplogger_trip_table")    suspend fun deleteAll()}