package com.koso.triplogger.dbimport androidx.lifecycle.LiveDataimport androidx.room.*@Daointerface TripRawDao {    @Query("SELECT * from triplogger_trip_table ORDER BY id DESC")    fun getAllLiveData(): LiveData<List<TripRaw>>    @Query("SELECT * from triplogger_trip_table ORDER BY id DESC")    suspend fun getAllSuspend(): List<TripRaw>    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insert(trip: TripRaw)    @Query("DELETE FROM triplogger_trip_table")    suspend fun deleteAll()    @Delete    suspend fun delete(trip: TripRaw)}