package com.koso.triplogger.dbimport androidx.lifecycle.LiveDataimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.withContextclass TripRepository(private val dao: TripRawDao) {    val allTrips: LiveData<List<TripRaw>> = dao.getAllLiveData()    suspend fun getAllSuspend() = withContext(Dispatchers.IO) { dao.getAllSuspend() }    suspend fun insert(trip: TripRaw) = withContext(Dispatchers.IO) {        dao.insert(trip)    }}