package com.koso.triplogger.dbimport android.content.Contextimport androidx.lifecycle.LiveDataimport com.koso.triplogger.io.TripFileHelperimport com.koso.triplogger.model.Tripimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.withContextclass TripRepository(private val dao: TripRawDao) {    val allTrips: LiveData<List<TripRaw>> = dao.getAllLiveData()    suspend fun getAllSuspend() = withContext(Dispatchers.IO) { dao.getAllSuspend() }    suspend fun insert(trip: TripRaw) = withContext(Dispatchers.IO) {        dao.insert(trip)    }    suspend fun delete(context: Context, trip: TripRaw){        withContext(Dispatchers.IO){            TripFileHelper.deleteFile(context, trip.filename)            dao.delete(trip)        }    }    suspend fun delete(context: Context, trip: Trip){        withContext(Dispatchers.IO){            TripFileHelper.deleteFile(context, trip.filename!!)            dao.deleteById(trip.id)        }    }}