package com.koso.triplogger.ioimport android.content.Contextimport com.koso.triplogger.model.Tripimport com.koso.triplogger.model.TripJsonAdapterimport com.squareup.moshi.Moshiimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.withContextimport java.io.Fileclass TripFileHelper {    companion object {        /**         * To write trip into local persist storage with json format         */        suspend fun writeToLocal(context: Context, filename: String, trip: Trip): Boolean {            return withContext(Dispatchers.IO) {                val adapter = TripJsonAdapter(Moshi.Builder().build())                val contentText = adapter.toJson(trip)                val file = File(context.filesDir, filename)                file.writeText(contentText)                true            }        }        suspend fun readFromLocal(context: Context, filename: String): Trip? {            return withContext(Dispatchers.IO) {                val adapter = TripJsonAdapter(Moshi.Builder().build())                val file = File(context.filesDir, filename)                val content = file.readText()                adapter.fromJson(content)            }        }        suspend fun deleteFile(context: Context, filename: String): Boolean {            return withContext(Dispatchers.IO){                val file = File(context.filesDir, filename)                try {                    file.delete()                }catch (e: Exception){                    false                }            }        }    }}